CREATE DATABASE /*!32312 IF NOT EXISTS*/`yyxblog` /*!40100 DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci */;

USE `yyxblog`;

/*Table structure for table `yyx_comments` */

DROP TABLE IF EXISTS `yyx_comments`;

CREATE TABLE `yyx_comments` (
  `coid` int(10) NOT NULL AUTO_INCREMENT,
  `pid` int(10) DEFAULT NULL,
  `time` date DEFAULT NULL,
  `user` varchar(200) COLLATE utf8_unicode_ci DEFAULT NULL,
  `mail` varchar(200) COLLATE utf8_unicode_ci DEFAULT NULL,
  `url` varchar(200) COLLATE utf8_unicode_ci DEFAULT NULL,
  `content` text COLLATE utf8_unicode_ci,
  `ip` varchar(64) COLLATE utf8_unicode_ci DEFAULT NULL,
  `agent` varchar(200) COLLATE utf8_unicode_ci DEFAULT NULL,
  PRIMARY KEY (`coid`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

/*Data for the table `yyx_comments` */

/*Table structure for table `yyx_links` */

DROP TABLE IF EXISTS `yyx_links`;

CREATE TABLE `yyx_links` (
  `lid` int(20) NOT NULL AUTO_INCREMENT,
  `lname` varchar(200) COLLATE utf8_unicode_ci DEFAULT NULL,
  `lurl` varchar(200) COLLATE utf8_unicode_ci DEFAULT NULL,
  `description` varchar(200) COLLATE utf8_unicode_ci DEFAULT NULL,
  PRIMARY KEY (`lid`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

/*Data for the table `yyx_links` */

insert  into `yyx_links`(`lid`,`lname`,`lurl`,`description`) values (1,'百度','http://www.baidu.com/',''),(2,'百度','http://www.baidu.com/','百度一下你就知道！');

/*Table structure for table `yyx_metas` */

DROP TABLE IF EXISTS `yyx_metas`;

CREATE TABLE `yyx_metas` (
  `mid` int(20) NOT NULL AUTO_INCREMENT,
  `name` varchar(200) COLLATE utf8_unicode_ci NOT NULL,
  `slug` varchar(200) COLLATE utf8_unicode_ci DEFAULT NULL,
  `type` varchar(32) COLLATE utf8_unicode_ci DEFAULT NULL,
  `description` varchar(200) COLLATE utf8_unicode_ci DEFAULT NULL,
  `count` int(10) DEFAULT '0',
  PRIMARY KEY (`mid`)
) ENGINE=InnoDB AUTO_INCREMENT=11 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

/*Data for the table `yyx_metas` */

insert  into `yyx_metas`(`mid`,`name`,`slug`,`type`,`description`,`count`) values (8,'默认分类','defeat','category','这是一个默认分类！',0),(9,'安卓技术','android','category','',0),(10,'软件技术','software','category','',0);

/*Table structure for table `yyx_posts` */

DROP TABLE IF EXISTS `yyx_posts`;

CREATE TABLE `yyx_posts` (
  `pid` int(20) NOT NULL AUTO_INCREMENT,
  `title` varchar(200) COLLATE utf8_unicode_ci DEFAULT NULL,
  `slug` varchar(200) COLLATE utf8_unicode_ci DEFAULT NULL,
  `status` varchar(16) COLLATE utf8_unicode_ci DEFAULT 'publish',
  `content` text COLLATE utf8_unicode_ci,
  `posttime` date DEFAULT NULL,
  `lasttime` date DEFAULT NULL,
  `ip` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL,
  `clicknum` int(11) DEFAULT '0',
  `authorid` varchar(10) COLLATE utf8_unicode_ci DEFAULT '1',
  PRIMARY KEY (`pid`)
) ENGINE=InnoDB AUTO_INCREMENT=60 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

/*Data for the table `yyx_posts` */

insert  into `yyx_posts`(`pid`,`title`,`slug`,`status`,`content`,`posttime`,`lasttime`,`ip`,`clicknum`,`authorid`) values (49,'第一篇日志','first log','publish','第一篇日志第一篇日志第一篇日志第一篇日志第一篇日志第一篇日志第一篇日志第一篇日志第一篇日志第一篇日志第一篇日志第一篇日志第一篇日志第一篇日志第一篇日志第一篇日志第一篇日志第一篇日志第一篇日志第一篇日志第一篇日志第一篇日志','2014-03-17','2014-03-17','127.0.0.1',0,NULL),(50,'第二篇日志','second log','publish','第二篇日志第二篇日志第二篇日志第二篇日志第二篇日志第二篇日志第二篇日志第二篇日志第二篇日志第二篇日志第二篇日志第二篇日志第二篇日志第二篇日志第二篇日志第二篇日志第二篇日志第二篇日志第二篇日志第二篇日志第二篇日志第二篇日志','2014-03-17','2014-03-17','127.0.0.1',0,NULL),(51,'第三篇日志','third log','publish','第三篇日志第三篇日志第三篇日志第三篇日志第三篇日志第三篇日志第三篇日志第三篇日志第三篇日志第三篇日志第三篇日志第三篇日志第三篇日志第三篇日志第三篇日志第三篇日志第三篇日志第三篇日志','2014-03-17','2014-03-17','127.0.0.1',0,'1'),(52,'wordpress本地测试站开启伪静态','wordpress-open-mod_rewrite','publish','<p style=\"text-indent:2em;\"><br />\r\n	首先打开httpd.conf 文件，找到LoadModule rewrite_module modules/mod_rewrite.so，去掉前面的#号。<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	找到你网站设置目录把 AllowOverride设置为All，默认显示的是None。<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	<br /><br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	/w/是我安装wp的目录。<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	接下来重启Apache就可以了。<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	<br /><br />\r\n</p>','2014-03-17','2014-03-17','127.0.0.1',0,'1'),(53,'wordpress本地测试站开启伪静态','wordpress-open-mod_rewrite','publish','<p style=\"text-indent:2em;\"><br />\r\n	首先打开httpd.conf 文件，找到LoadModule rewrite_module modules/mod_rewrite.so，去掉前面的#号。<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	找到你网站设置目录把 AllowOverride设置为All，默认显示的是None。\"\" “” “” ‘ ’ ‘ ’<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	<br /><br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	/w/是我安装wp的目录。<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	接下来重启Apache就可以了。<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	<br /><br />\r\n</p>','2014-03-17','2014-03-17','127.0.0.1',0,'1'),(54,'wordpress本地测试站开启伪静态','wordpress-open-mod_rewrite','publish','<p style=\"text-indent:2em;\"><br />\r\n	首先打开httpd.conf 文件，找到LoadModule rewrite_module modules/mod_rewrite.so，去掉前面的#号。<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	找到你网站设置目录把 AllowOverride设置为All，默认显示的是None。<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	&lt;Directory “E:/lamp/www”&gt;<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	#<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	<br /><br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	<br /><br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	<br /><br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	<br /><br />\r\n</p>','2014-03-17','2014-03-17','127.0.0.1',0,'1'),(55,'wordpress本地测试站开启伪静态','wordpress-open-mod_rewrite','publish','<p style=\"text-indent:2em;\"><br />\r\n	首先打开httpd.conf 文件，找到LoadModule rewrite_module modules/mod_rewrite.so，去掉前面的#号。<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	找到你网站设置目录把 AllowOverride设置为All，默认显示的是None。<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	&lt;Directory “E:/lamp/www”&gt;<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	<br /><br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	<br /><br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	<br /><br />\r\n</p><br />\r\n<p><br />\r\n	<br /><br />\r\n</p>','2014-03-17','2014-03-17','127.0.0.1',0,'1'),(56,'wordpress本地测试站开启伪静态','wordpress-open-mod_rewrite','publish','<p style=\"text-indent:2em;\"><br />\r\n	首先打开httpd.conf 文件，找到LoadModule rewrite_module modules/mod_rewrite.so，去掉前面的#号。<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	找到你网站设置目录把 AllowOverride设置为All，默认显示的是None。<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	&lt;Directory “E:/lamp/www”&gt;<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	<span># Possible values for the Options directive&nbsp;</span><span>are None&nbsp;All</span> <br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	<br /><br />\r\n</p><br />\r\n<p><br />\r\n	<br /><br />\r\n</p>','2014-03-17','2014-03-17','127.0.0.1',0,'1'),(57,'wordpress本地测试站开启伪静态','wordpress-open-mod_rewrite','publish','<p style=\"text-indent:2em;\"><br />\r\n	<br /><br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	首先打开httpd.conf 文件，找到LoadModule rewrite_module modules/mod_rewrite.so，去掉前面的#号。<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	找到你网站设置目录把 AllowOverride设置为All，默认显示的是None。<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	&lt;Directory “E:/lamp/www”&gt;<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	#<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	# Possible values for the Options directive are “None”， “All”，<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	# or any combination of:<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	#&nbsp;&nbsp; Indexes Includes FollowSymLinks SymLinksifOwnerMatch ExecCGI MultiViews<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	#<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	# Note that “MultiViews” must be named *explicitly* — “Options All”<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	# doesn’t give it to you.<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	#<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	# The Options directive is both complicated and important.&nbsp; Please see<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	# http://httpd.apache.org/docs/2.2/mod/core.html#options<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	# for more information.<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	#<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	Options Indexes FollowSymLinks<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	#<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	# AllowOverride controls what directives may be placed in .htaccess files.<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	# It can be “All”， “None”， or any combination of the keywords:<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	#&nbsp;&nbsp; Options FileInfo AuthConfig Limit<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	#<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	AllowOverride All<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	#<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	# Controls who can get stuff from this server.<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	#<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	Order allow，deny<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	Allow from all<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	&lt;/Directory&gt;<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	再去后台设置静态规则，我设置为这样的/\\%postname\\%.html 。<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	接下来看看网站根目录会自动生成.htaccess文件里面会写入如下内容<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	# BEGIN WordPress<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	&lt;IfModule mod_rewrite.c&gt;<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	RewriteEngine On<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	RewriteBase /w/<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	RewriteRule ^index\\.php$ – [L]<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	RewriteCond \\%{REQUEST_FILENAME} !-f<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	RewriteCond \\%{REQUEST_FILENAME} !-d<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	RewriteRule . /w/index.php [L]<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	&lt;/IfModule&gt;<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	# END WordPress<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	/w/是我安装wp的目录。<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	接下来重启Apache就可以了。<br />\r\n</p><br />\r\n<p style=\"text-indent:2em;\"><br />\r\n	<br /><br />\r\n</p>','2014-03-17','2014-03-17','127.0.0.1',0,'1'),(58,'JQuery和CSS 版wordpress 弹性伸缩搜索框','jquery-and-css-search-box','publish','<p style=\"font-family:\'Microsoft Yahei\'， Tahoma， Helvetica， Arial， SimSun， sans-serif;font-size:14px;\"><br />\r\n	我们知道wordpress 官方主题采用了H5，还有就是那个弹性的搜索框非常好看，我就来说说怎么么用JQuery和CSS 来实习wordpress弹性搜索框，官方是用css实现的，但在IE内核下显示就不好了，用JQuery 来做就可以兼容主流浏览器。<br />\r\n</p><br />\r\n<p style=\"font-family:\'Microsoft Yahei\'， Tahoma， Helvetica， Arial， SimSun， sans-serif;font-size:14px;\"><br />\r\n	css版我就直接贴上代码吧供大家参考<br />\r\n</p><br />\r\n<p style=\"font-family:\'Microsoft Yahei\'， Tahoma， Helvetica， Arial， SimSun， sans-serif;font-size:14px;\"><br />\r\n	<br /><br />\r\n</p><br />\r\n<pre class=\"prettyprint lang-php\">&lt;!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\"&gt;<br />\r\n&lt;html xmlns=\"http://www.w3.org/1999/xhtml\"&gt;<br />\r\n&lt;head&gt;<br />\r\n&lt;meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\" /&gt;<br />\r\n&lt;title&gt;wordpress CSS搜索伸缩菜单&lt;/title&gt;<br />\r\n&lt;style&gt;<br />\r\n/*search*/<br />\r\ninput， select {<br />\r\n    padding:6px;<br />\r\n    border:1px solid;<br />\r\n    border-color:#CCC #EEE #EEE #CCC;<br />\r\n    -moz-border-radius:4px;<br />\r\n    -webkit-border-radius:4px;<br />\r\n    border-radius:4px;<br />\r\n    -moz-box-shadow: inset 0 1px 2px rgba(0， 0， 0， 0.1)， 0 1px 0 rgba(255， 255， 255， 0.2);<br />\r\n    -webkit-box-shadow: inset 0 1px 2px rgba(0， 0， 0， 0.1)， 0 1px 0 rgba(255， 255， 255， 0.2);<br />\r\n    box-shadow: inset 0 1px 2px rgba(0， 0， 0， 0.1)， 0 1px 0 rgba(255， 255， 255， 0.2);<br />\r\n    background-color:#fafafa;<br />\r\n    font-size:12px;<br />\r\n    color:#555;<br />\r\n    outline:none;<br />\r\n    behavior:url(/wp-content/themes/EchoWeb/lib/PIE.htc);<br />\r\n-moz-transition:all linear .5s;<br />\r\n-webkit-transition:all linear .5s;<br />\r\n-o-transition:all linear .5s;<br />\r\ntransition:all linear .5s<br />\r\n}<br />\r\ninput:hover， select:hover， textarea:hover {<br />\r\n    color:#333;<br />\r\n    background-color:#fff<br />\r\n}<br />\r\ninput:focus {<br />\r\n    border-color: rgba(82， 168， 236， .75);<br />\r\n    box-shadow: 0 0 8px rgba(82， 168， 236， .5);<br />\r\n    -moz-box-shadow: 0 0 8px rgba(82， 168， 236， .5);<br />\r\n    -webkit-box-shadow: 0 0 8px rgba(82， 168， 236， .5);<br />\r\n    background-color:#fff<br />\r\n}<br />\r\n <br />\r\n#searchform {<br />\r\n    right: 12\\%;<br />\r\n    padding-top: 2px;<br />\r\n    text-align: right;<br />\r\n}<br />\r\n.assistive-text {<br />\r\n    clip: rect(1px， 1px， 1px， 1px);<br />\r\n    position: absolute !important;<br />\r\n}<br />\r\n#s {<br />\r\n    -moz-transition-duration: 400ms;<br />\r\n    -moz-transition-property: width， background;<br />\r\n    -moz-transition-timing-function: ease;<br />\r\n    float: right;<br />\r\n    width: 72px;<br />\r\n}<br />\r\ninput#s {<br />\r\n    background: url(img/search.png) no-repeat scroll 5px 6px transparent;<br />\r\n    border-radius: 2px 2px 2px 2px;<br />\r\n    font-size: 14px;<br />\r\n    height: 22px;<br />\r\n    line-height: 1.2em;<br />\r\n    padding: 4px 10px 4px 28px;<br />\r\n}<br />\r\n#searchsubmit {<br />\r\n    display: none;<br />\r\n}<br />\r\ninput#searchsubmit {<br />\r\n    display: none;<br />\r\n}<br />\r\n#s {<br />\r\n    float: right;<br />\r\n    -webkit-transition-duration: 400ms;<br />\r\n    -webkit-transition-property: width， background;<br />\r\n    -webkit-transition-timing-function: ease;<br />\r\n    