CREATE TABLE `yyx_relationships` (
  `pid` int(10) NOT NULL,
  `mid` int(10) NOT NULL,
  PRIMARY KEY (`pid`,`mid`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

