/*Table structure for table `yyx_users` */

DROP TABLE IF EXISTS `yyx_users`;

CREATE TABLE `yyx_users` (
  `uid` int(20) NOT NULL AUTO_INCREMENT,
  `username` varchar(32) COLLATE utf8_unicode_ci DEFAULT NULL,
  `nikename` varchar(32) COLLATE utf8_unicode_ci DEFAULT NULL,
  `userpass` varchar(64) COLLATE utf8_unicode_ci DEFAULT NULL,
  `mail` varchar(200) COLLATE utf8_unicode_ci DEFAULT NULL,
  `url` varchar(200) COLLATE utf8_unicode_ci DEFAULT NULL,
  `registertime` date DEFAULT NULL,
  `updatetime` date DEFAULT NULL,
  `ip` varchar(32) COLLATE utf8_unicode_ci DEFAULT NULL,
  `group` varchar(16) COLLATE utf8_unicode_ci DEFAULT '1',
  PRIMARY KEY (`uid`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

